HAL_StatusTypeDef HAL_LCD_Write ( LCD_HandleTypeDef * hlcd , uint32_t RAMRegisterIndex , uint32_t RAMRegisterMask , uint32_t Data ) { uint32_t tickstart = 0x00 ; if ( ( hlcd -> State == HAL_LCD_STATE_READY ) || ( hlcd -> State == HAL_LCD_STATE_BUSY ) ) { assert_param ( IS_LCD_RAM_REGISTER ( RAMRegisterIndex ) ) ; MODIFY_REG ( hlcd -> Instance -> RAM [ RAMRegisterIndex ] , ~ ( RAMRegisterMask ) , Data ) ; return HAL_OK ; } else { return HAL_ERROR ; } } 