static void rk3x_i2c_stop ( struct rk3x_i2c * i2c , int error ) { unsigned int ctrl ; i2c -> processed = 0 ; i2c -> error = error ; if ( i2c -> is_last_msg ) { i2c_writel ( i2c , REG_INT_STOP , REG_IEN ) ; i2c -> state = STATE_STOP ; ctrl = i2c_readl ( i2c , REG_CON ) ; ctrl |= REG_CON_STOP ; i2c_writel ( i2c , ctrl , REG_CON ) ; } else { i2c -> busy = false ; i2c -> state = STATE_IDLE ; ctrl = i2c_readl ( i2c , REG_CON ) & REG_CON_TUNING_MASK ; i2c_writel ( i2c , ctrl , REG_CON ) ; wake_up ( & i2c -> wait ) ; } } 