static int compat_setend_handler ( struct pt_regs * regs , u32 big_endian ) { char * insn ; if ( big_endian ) { insn = "setend be" ; regs -> pstate |= PSR_AA32_E_BIT ; } else { insn = "setend le" ; regs -> pstate &= ~ PSR_AA32_E_BIT ; } trace_instruction_emulation ( insn , regs -> pc ) ; pr_warn_ratelimited ( "\"%s\" (%ld) uses deprecated setend instruction at 0x%llx\n" , current -> comm , ( unsigned long ) current -> pid , regs -> pc ) ; return 0 ; } 