static void iscsi_enqueue_cmd_tail ( iscsi_cmd_t * * head , iscsi_cmd_t * * tail , iscsi_cmd_t * icmdp ) { ASSERT ( icmdp != NULL ) ; ASSERT ( icmdp -> cmd_next == NULL ) ; ASSERT ( icmdp -> cmd_prev == NULL ) ; ASSERT ( icmdp != * head ) ; if ( * head == NULL ) { * head = * tail = icmdp ; icmdp -> cmd_prev = NULL ; icmdp -> cmd_next = NULL ; } else { icmdp -> cmd_next = NULL ; icmdp -> cmd_prev = * tail ; ( * tail ) -> cmd_next = icmdp ; * tail = icmdp ; } } 