static void rs_tiff_class_init ( RSTiffClass * klass ) { GObjectClass * object_class = G_OBJECT_CLASS ( klass ) ; object_class -> get_property = rs_tiff_get_property ; object_class -> set_property = rs_tiff_set_property ; object_class -> dispose = rs_tiff_dispose ; object_class -> finalize = rs_tiff_finalize ; g_object_class_install_property ( object_class , PROP_FILENAME , g_param_spec_string ( "filename" , "Filename" , "The filename to load" , NULL , G_PARAM_CONSTRUCT_ONLY | G_PARAM_READWRITE ) ) ; } 