void amdgpu_dm_irq_unregister_interrupt ( struct amdgpu_device * adev , enum dc_irq_source irq_source , void * ih ) { struct list_head * handler_list ; struct dc_interrupt_params int_params ; int i ; if ( false == validate_irq_unregistration_params ( irq_source , ih ) ) { return ; } int_params . irq_source = irq_source ; for ( i = 0 ; i < INTERRUPT_CONTEXT_NUMBER ; i ++ ) { int_params . int_context = i ; handler_list = remove_irq_handler ( adev , ih , & int_params ) ; if ( handler_list != NULL ) { break ; } } if ( handler_list == NULL ) { DRM_ERROR ( "DM_IRQ: failed to find irq handler:%p for irq_source:%d!\n" , ih , irq_source ) ; } } 