void win_handle_command_list ( ProfWin * window , GSList * cmds ) { assert ( window != NULL ) ; if ( cmds ) { win_println ( window , THEME_DEFAULT , "!" , "Ad hoc commands:" ) ; GSList * curr_cmd = cmds ; while ( curr_cmd ) { win_println ( window , THEME_DEFAULT , "!" , "  %s" , cmd ) ; curr_cmd = g_slist_next ( curr_cmd ) ; } win_println ( window , THEME_DEFAULT , "!" , "" ) ; } else { win_println ( window , THEME_DEFAULT , "!" , "No commands found" ) ; win_println ( window , THEME_DEFAULT , "!" , "" ) ; } } 