static void dp_aux_i2c_handler ( struct dp_aux_private * aux , u32 isr ) { if ( isr & DP_INTR_AUX_I2C_DONE ) { if ( isr & ( DP_INTR_I2C_NACK | DP_INTR_I2C_DEFER ) ) { aux -> aux_error_num = DP_AUX_ERR_NACK ; } else { aux -> aux_error_num = DP_AUX_ERR_NONE ; } } else { if ( isr & DP_INTR_WRONG_ADDR ) { aux -> aux_error_num = DP_AUX_ERR_ADDR ; } if ( isr & DP_INTR_TIMEOUT ) { aux -> aux_error_num = DP_AUX_ERR_TOUT ; } if ( isr & DP_INTR_NACK_DEFER ) { aux -> aux_error_num = DP_AUX_ERR_NACK_DEFER ; } if ( isr & DP_INTR_I2C_NACK ) { aux -> aux_error_num = DP_AUX_ERR_NACK ; } if ( isr & DP_INTR_I2C_DEFER ) { aux -> aux_error_num = DP_AUX_ERR_DEFER ; } if ( isr & DP_INTR_AUX_ERROR ) { aux -> aux_error_num = DP_AUX_ERR_PHY ; } } } 