static int get_fmt_from_name ( const char * s ) { if ( ! strcmp ( s , "png" ) ) { return IW_FORMAT_PNG ; } if ( ! strcmp ( s , "jpg" ) ) { return IW_FORMAT_JPEG ; } if ( ! strcmp ( s , "jpeg" ) ) { return IW_FORMAT_JPEG ; } if ( ! strcmp ( s , "bmp" ) ) { return IW_FORMAT_BMP ; } if ( ! strcmp ( s , "tif" ) ) { return IW_FORMAT_TIFF ; } if ( ! strcmp ( s , "tiff" ) ) { return IW_FORMAT_TIFF ; } if ( ! strcmp ( s , "miff" ) ) { return IW_FORMAT_MIFF ; } if ( ! strcmp ( s , "webp" ) ) { return IW_FORMAT_WEBP ; } if ( ! strcmp ( s , "pnm" ) ) { return IW_FORMAT_PNM ; } if ( ! strcmp ( s , "ppm" ) ) { return IW_FORMAT_PPM ; } if ( ! strcmp ( s , "pgm" ) ) { return IW_FORMAT_PGM ; } if ( ! strcmp ( s , "pbm" ) ) { return IW_FORMAT_PBM ; } if ( ! strcmp ( s , "pam" ) ) { return IW_FORMAT_PAM ; } return IW_FORMAT_UNKNOWN ; } 