void rtl8192ce_read_bt_coexist_info_from_hwpg ( struct ieee80211_hw * hw , bool auto_load_fail , u8 * hwinfo ) { struct rtl_priv * rtlpriv = rtl_priv ( hw ) ; u8 val ; if ( ! auto_load_fail ) { rtlpriv -> btcoexist . eeprom_bt_coexist = ( ( hwinfo [ RF_OPTION1 ] & 0xe0 ) >> 5 ) ; val = hwinfo [ RF_OPTION4 ] ; rtlpriv -> btcoexist . eeprom_bt_type = ( ( val & 0xe ) >> 1 ) ; rtlpriv -> btcoexist . eeprom_bt_ant_num = ( val & 0x1 ) ; rtlpriv -> btcoexist . eeprom_bt_ant_isol = ( ( val & 0x10 ) >> 4 ) ; rtlpriv -> btcoexist . eeprom_bt_radio_shared = ( ( val & 0x20 ) >> 5 ) ; } else { rtlpriv -> btcoexist . eeprom_bt_coexist = 0 ; rtlpriv -> btcoexist . eeprom_bt_type = BT_2WIRE ; rtlpriv -> btcoexist . eeprom_bt_ant_num = ANT_X2 ; rtlpriv -> btcoexist . eeprom_bt_ant_isol = 0 ; rtlpriv -> btcoexist . eeprom_bt_radio_shared = BT_RADIO_SHARED ; } } 