static void display_sdram_details ( u32 emif_nr , u32 cs , struct lpddr2_device_details * device ) { const char * mfg_str ; const char * type_str ; char density_str [ 10 ] ; u32 density ; debug ( "EMIF%d CS%d\t" , emif_nr , cs ) ; if ( ! device ) { debug ( "None\n" ) ; return ; } mfg_str = get_lpddr2_manufacturer ( device -> manufacturer ) ; type_str = get_lpddr2_type ( device -> type ) ; density = lpddr2_density_2_size_in_mbytes [ device -> density ] ; if ( ( density / 1024 * 1024 ) == density ) { density /= 1024 ; sprintf ( density_str , "%d GB" , density ) ; } else { sprintf ( density_str , "%d MB" , density ) ; } if ( mfg_str && type_str ) { debug ( "%s\t\t%s\t%s\n" , mfg_str , type_str , 0 ) ; } } 