static void add_labels_to_disk ( struct disk * d , RRDSET * st ) { rrdlabels_add ( st -> rrdlabels , "device" , d -> disk , RRDLABEL_SRC_AUTO ) ; switch ( d -> type ) { default : case DISK_TYPE_UNKNOWN : rrdlabels_add ( st -> rrdlabels , "device_type" , "unknown" , RRDLABEL_SRC_AUTO ) ; break ; case DISK_TYPE_PHYSICAL : rrdlabels_add ( st -> rrdlabels , "device_type" , "physical" , RRDLABEL_SRC_AUTO ) ; break ; case DISK_TYPE_PARTITION : rrdlabels_add ( st -> rrdlabels , "device_type" , "partition" , RRDLABEL_SRC_AUTO ) ; break ; case DISK_TYPE_VIRTUAL : rrdlabels_add ( st -> rrdlabels , "device_type" , "virtual" , RRDLABEL_SRC_AUTO ) ; break ; } } 