static int setup_port ( struct serial_private * priv , struct uart_8250_port * port , int bar , int offset , int regshift ) { struct pci_dev * dev = priv -> dev ; if ( pci_resource_flags ( dev , bar ) & IORESOURCE_MEM ) { if ( ! pcim_iomap ( dev , bar , 0 ) && ! pcim_iomap_table ( dev ) ) { return - ENOMEM ; } port -> port . iotype = UPIO_MEM ; port -> port . iobase = 0 ; port -> port . mapbase = pci_resource_start ( dev , bar ) + offset ; port -> port . membase = pcim_iomap_table ( dev ) [ bar ] + offset ; port -> port . regshift = regshift ; } else { port -> port . iotype = UPIO_PORT ; port -> port . iobase = pci_resource_start ( dev , bar ) + offset ; port -> port . mapbase = 0 ; port -> port . membase = NULL ; port -> port . regshift = 0 ; } return 0 ; } 