void isst_clos_display_information ( struct isst_id * id , FILE * outf , int clos , struct isst_clos_config * clos_config ) { char header [ 256 ] ; char value [ 256 ] ; int level ; level = print_package_info ( id , outf ) ; snprintf ( header , sizeof ( header ) , "core-power" ) ; snprintf ( header , sizeof ( header ) , "clos" ) ; snprintf ( value , sizeof ( value ) , "%d" , clos ) ; format_and_print ( outf , level + 2 , header , value ) ; snprintf ( header , sizeof ( header ) , "epp" ) ; snprintf ( value , sizeof ( value ) , "%d" , clos_config -> epp ) ; format_and_print ( outf , level + 2 , header , value ) ; snprintf ( header , sizeof ( header ) , "clos-proportional-priority" ) ; snprintf ( value , sizeof ( value ) , "%d" , clos_config -> clos_prop_prio ) ; format_and_print ( outf , level + 2 , header , value ) ; snprintf ( header , sizeof ( header ) , "clos-min" ) ; snprintf ( value , sizeof ( value ) , "%d MHz" , clos_config -> clos_min * DISP_FREQ_MULTIPLIER ) ; format_and_print ( outf , level + 2 , header , value ) ; snprintf ( header , sizeof ( header ) , "clos-max" ) ; if ( clos_config -> clos_max == 0xff ) { snprintf ( value , sizeof ( value ) , "Max Turbo frequency" ) ; } else { snprintf ( value , sizeof ( value ) , "%d MHz" , clos_config -> clos_max * DISP_FREQ_MULTIPLIER ) ; } format_and_print ( outf , level + 2 , header , value ) ; snprintf ( header , sizeof ( header ) , "clos-desired" ) ; snprintf ( value , sizeof ( value ) , "%d MHz" , clos_config -> clos_desired * DISP_FREQ_MULTIPLIER ) ; format_and_print ( outf , level + 2 , header , value ) ; format_and_print ( outf , level , NULL , NULL ) ; } 