void ath9k_btcoex_timer_resume ( struct ath_softc * sc ) { struct ath_btcoex * btcoex = & sc -> btcoex ; struct ath_hw * ah = sc -> sc_ah ; if ( ath9k_hw_get_btcoex_scheme ( ah ) != ATH_BTCOEX_CFG_3WIRE && ath9k_hw_get_btcoex_scheme ( ah ) != ATH_BTCOEX_CFG_MCI ) { return ; } ath_dbg ( ath9k_hw_common ( ah ) , BTCOEX , "Starting btcoex timers\n" ) ; btcoex -> bt_priority_cnt = 0 ; btcoex -> bt_priority_time = jiffies ; clear_bit ( BT_OP_PRIORITY_DETECTED , & btcoex -> op_flags ) ; clear_bit ( BT_OP_SCAN , & btcoex -> op_flags ) ; mod_timer ( & btcoex -> period_timer , jiffies ) ; } 