static inline void __pm_clk_enable ( struct device * dev , struct pm_clock_entry * ce ) { int ret ; switch ( ce -> status ) { case PCE_STATUS_ACQUIRED : break ; case PCE_STATUS_PREPARED : ret = clk_enable ( ce -> clk ) ; break ; default : return ; } if ( ! ret ) { ce -> status = PCE_STATUS_ENABLED ; } else { dev_err ( dev , "%s: failed to enable clk %p, error %d\n" , __func__ , ce -> clk , ret ) ; } } 