bool virDomainDeviceAddressIsValid ( virDomainDeviceInfo * info , int type ) { switch ( info -> type ) { case VIR_DOMAIN_DEVICE_ADDRESS_TYPE_PCI : return virPCIDeviceAddressIsValid ( & info -> addr . pci , false ) ; case VIR_DOMAIN_DEVICE_ADDRESS_TYPE_DRIVE : return true ; case VIR_DOMAIN_DEVICE_ADDRESS_TYPE_VIRTIO_S390 : case VIR_DOMAIN_DEVICE_ADDRESS_TYPE_VIRTIO_MMIO : return true ; case VIR_DOMAIN_DEVICE_ADDRESS_TYPE_CCW : return virCCWDeviceAddressIsValid ( & info -> addr . ccw ) ; case VIR_DOMAIN_DEVICE_ADDRESS_TYPE_USB : return true ; } return false ; } 