static inline int qedr_check_gsi_qp_attrs ( struct qedr_dev * dev , struct ib_qp_init_attr * attrs ) { if ( attrs -> cap . max_recv_sge > QEDR_GSI_MAX_RECV_SGE ) { DP_ERR ( dev , " create gsi qp: failed. max_recv_sge is larger the max %d>%d\n" , attrs -> cap . max_recv_sge , QEDR_GSI_MAX_RECV_SGE ) ; return - EINVAL ; } if ( attrs -> cap . max_recv_wr > QEDR_GSI_MAX_RECV_WR ) { DP_ERR ( dev , " create gsi qp: failed. max_recv_wr is too large %d>%d\n" , attrs -> cap . max_recv_wr , QEDR_GSI_MAX_RECV_WR ) ; return - EINVAL ; } return 0 ; } 