static char * sec_flags2str ( unsigned long flags , char * buf , int bufsize ) { buf [ 0 ] = '\0' ; if ( flags & PTLRPC_SEC_FL_REVERSE ) { strlcat ( buf , "reverse," , bufsize ) ; } if ( flags & PTLRPC_SEC_FL_ROOTONLY ) { strlcat ( buf , "rootonly," , bufsize ) ; } if ( flags & PTLRPC_SEC_FL_UDESC ) { strlcat ( buf , "udesc," , bufsize ) ; } if ( flags & PTLRPC_SEC_FL_BULK ) { strlcat ( buf , "bulk," , bufsize ) ; } return buf ; } 