static enum nv50_crc_source_type nv50_crc_source_type ( struct nouveau_encoder * outp , enum nv50_crc_source source ) { struct dcb_output * dcbe = outp -> dcb ; switch ( source ) { case NV50_CRC_SOURCE_NONE : return NV50_CRC_SOURCE_TYPE_NONE ; case NV50_CRC_SOURCE_RG : return NV50_CRC_SOURCE_TYPE_RG ; default : break ; } switch ( dcbe -> type ) { case DCB_OUTPUT_DP : return NV50_CRC_SOURCE_TYPE_SF ; case DCB_OUTPUT_ANALOG : return NV50_CRC_SOURCE_TYPE_DAC ; default : return NV50_CRC_SOURCE_TYPE_SOR ; } } 