static int keyring_store ( struct credential * c ) { guint32 item_id ; char * object = NULL ; GnomeKeyringResult result ; if ( ! c -> protocol || ! ( c -> host || c -> path ) || ! c -> username || ! c -> password ) { return EXIT_FAILURE ; } object = keyring_object ( c ) ; result = gnome_keyring_set_network_password_sync ( GNOME_KEYRING_DEFAULT , c -> username , NULL , c -> host , object , c -> protocol , NULL , c -> port , c -> password , & item_id ) ; if ( result != GNOME_KEYRING_RESULT_OK && result != GNOME_KEYRING_RESULT_CANCELLED ) { g_critical ( "%s" , gnome_keyring_result_to_message ( result ) ) ; return EXIT_FAILURE ; } return EXIT_SUCCESS ; } 