int pcmdlist ( void ) { register const struct cmd * cp ; register int cc ; printf ( "Commands are:\n" ) ; for ( cc = 0 , cp = cmdtab ; cp -> c_name != NULL ; cp ++ ) { cc += strlen ( cp -> c_name ) + 2 ; if ( cc > 72 ) { cc = strlen ( cp -> c_name ) + 2 ; } if ( ( cp + 1 ) -> c_name != NOSTR ) { printf ( "%s, " , cp -> c_name ) ; } else { printf ( "%s\n" , cp -> c_name ) ; } } return ( 0 ) ; } 