int ff_rtp_get_payload_type ( const AVFormatContext * fmt , const AVCodecParameters * par , int idx ) { int i ; const AVOutputFormat * ofmt = fmt ?fmt -> oformat : NULL ; if ( ofmt && ofmt -> priv_class && fmt -> priv_data ) { int64_t payload_type ; if ( av_opt_get_int ( fmt -> priv_data , "payload_type" , 0 , & payload_type ) >= 0 && payload_type >= 0 ) { return ( int ) payload_type ; } } for ( i = 0 ; rtp_payload_types [ i ] . pt >= 0 ; ++ i ) { if ( rtp_payload_types [ i ] . codec_id == par -> codec_id ) { if ( par -> codec_id == AV_CODEC_ID_H263 && ( ! fmt || ! fmt -> oformat || ! fmt -> oformat -> priv_class || ! fmt -> priv_data || ! av_opt_flag_is_set ( fmt -> priv_data , "rtpflags" , "rfc2190" ) ) ) { continue ; } if ( par -> codec_id == AV_CODEC_ID_ADPCM_G722 && par -> sample_rate == 16000 && par -> ch_layout . nb_channels == 1 ) { return rtp_payload_types [ i ] . pt ; } if ( par -> codec_type == AVMEDIA_TYPE_AUDIO && ( ( rtp_payload_types [ i ] . clock_rate > 0 && par -> sample_rate != rtp_payload_types [ i ] . clock_rate ) || ( rtp_payload_types [ i ] . audio_channels > 0 && par -> ch_layout . nb_channels != rtp_payload_types [ i ] . audio_channels ) ) ) { continue ; } return rtp_payload_types [ i ] . pt ; } } return RTP_PT_PRIVATE + idx ; } 