static void lpphy_enable_rx_gain_override ( struct b43_wldev * dev ) { b43_phy_set ( dev , B43_LPPHY_RF_OVERRIDE_0 , 0x1 ) ; b43_phy_set ( dev , B43_LPPHY_RF_OVERRIDE_0 , 0x10 ) ; b43_phy_set ( dev , B43_LPPHY_RF_OVERRIDE_0 , 0x40 ) ; if ( dev -> phy . rev >= 2 ) { b43_phy_set ( dev , B43_LPPHY_RF_OVERRIDE_2 , 0x100 ) ; if ( b43_current_band ( dev -> wl ) == NL80211_BAND_2GHZ ) { b43_phy_set ( dev , B43_LPPHY_RF_OVERRIDE_2 , 0x400 ) ; } } else { b43_phy_set ( dev , B43_LPPHY_RF_OVERRIDE_2 , 0x200 ) ; } } 