static void infd_request_set_property ( GObject * object , guint prop_id , const GValue * value , GParamSpec * pspec ) { InfdRequest * request ; InfdRequestPrivate * priv ; request = INFD_REQUEST ( object ) ; priv = INFD_REQUEST_PRIVATE ( request ) ; switch ( prop_id ) { case PROP_TYPE : g_assert ( priv -> type == NULL ) ; priv -> type = g_value_dup_string ( value ) ; break ; case PROP_NODE_ID : g_assert ( priv -> node_id == G_MAXUINT ) ; priv -> node_id = g_value_get_uint ( value ) ; break ; case PROP_REQUESTOR : priv -> requestor = g_value_dup_object ( value ) ; break ; case PROP_PROGRESS : default : G_OBJECT_WARN_INVALID_PROPERTY_ID ( object , prop_id , pspec ) ; break ; } } 