static void addarg ( struct arglist * al , const char * arg ) { if ( al -> argc == al -> maxc ) { al -> argv = realloc ( al -> argv , al -> maxc * sizeof ( char * ) ) ; if ( al -> argv == NULL ) { err ( EX_TEMPFAIL , "realloc" ) ; } } if ( arg == NULL ) { al -> argv [ al -> argc ++ ] = NULL ; } if ( ( al -> argv [ al -> argc ++ ] = strdup ( arg ) ) == NULL ) { err ( EX_TEMPFAIL , "strdup" ) ; } } 