static int daemon_get_shufport ( struct daemon * daemon , int * shufport ) { int i , n , k , temp ; int avail = 0 ; for ( i = 0 ; i < 65536 ; i ++ ) { if ( daemon -> cfg -> outgoing_avail_ports [ i ] ) { shufport [ avail ++ ] = daemon -> cfg -> outgoing_avail_ports [ i ] ; } } n = avail ; while ( -- n > 0 ) { k = ub_random_max ( daemon -> rand , n + 1 ) ; temp = shufport [ k ] ; shufport [ k ] = shufport [ n ] ; shufport [ n ] = temp ; } return avail ; } 