static int rpc_mod_print_all ( rpc_t * rpc , void * ctx , mem_counter * pkg_stats , mem_counter * shm_stats , int flag ) { mem_counter * pkg_iter = pkg_stats ; mem_counter * shm_iter = shm_stats ; while ( pkg_iter ) { if ( ! rpc_mod_is_printed_one ( pkg_stats , pkg_iter ) ) { rpc_mod_print_one ( rpc , ctx , pkg_iter -> mname , pkg_stats , shm_stats , flag ) ; } pkg_iter = pkg_iter -> next ; } while ( shm_iter ) { shm_iter = shm_iter -> next ; } return 0 ; } 