static int cdns3_role_start ( struct cdns3 * cdns , enum usb_role role ) { int ret ; mutex_lock ( & cdns -> mutex ) ; cdns -> role = role ; mutex_unlock ( & cdns -> mutex ) ; if ( ! cdns -> roles [ role ] ) { return - ENXIO ; } if ( cdns -> roles [ role ] -> state == CDNS3_ROLE_STATE_ACTIVE ) { return 0 ; } mutex_lock ( & cdns -> mutex ) ; ret = cdns -> roles [ role ] -> start ( cdns ) ; if ( ! ret ) { cdns -> roles [ role ] -> state = CDNS3_ROLE_STATE_ACTIVE ; } mutex_unlock ( & cdns -> mutex ) ; return ret ; } 