static void synaptics_pt_create ( struct psmouse * psmouse ) { struct serio * serio ; serio = kzalloc ( sizeof ( serio ) , GFP_KERNEL ) ; if ( ! serio ) { psmouse_err ( psmouse , "not enough memory for pass-through port\n" ) ; return ; } serio -> id . type = SERIO_PS_PSTHRU ; strscpy ( serio -> name , "Synaptics pass-through" , sizeof ( serio -> name ) ) ; serio -> write = synaptics_pt_write ; serio -> start = synaptics_pt_start ; serio -> stop = synaptics_pt_stop ; serio -> parent = psmouse -> ps2dev . serio ; psmouse -> pt_activate = synaptics_pt_activate ; psmouse_info ( psmouse , "serio: %s port at %s\n" , serio -> name , psmouse -> phys ) ; serio_register_port ( serio ) ; } 