void test_stash_apply__fails_with_uncommitted_changes_in_index ( void ) { cl_git_rewritefile ( "stash/who" , "nothing\n" ) ; cl_git_pass ( git_index_add_bypath ( repo_index , "who" ) ) ; cl_git_fail_with ( git_stash_apply ( repo , 0 , NULL ) , GIT_EUNCOMMITTED ) ; cl_assert_equal_i ( git_index_has_conflicts ( repo_index ) , 0 ) ; assert_status ( repo , "what" , GIT_STATUS_CURRENT ) ; assert_status ( repo , "how" , GIT_STATUS_CURRENT ) ; assert_status ( repo , "who" , GIT_STATUS_INDEX_MODIFIED ) ; assert_status ( repo , "when" , GIT_ENOTFOUND ) ; assert_status ( repo , "why" , GIT_ENOTFOUND ) ; } 