void test_str_basic__resize_incremental ( void ) { git_str buf1 = GIT_STR_INIT ; cl_git_pass ( git_str_puts ( & buf1 , "Hello" ) ) ; cl_assert_equal_i ( 5 , buf1 . size ) ; cl_assert_equal_i ( 8 , buf1 . asize ) ; cl_git_pass ( git_str_grow_by ( & buf1 , 1 ) ) ; cl_assert_equal_i ( 5 , buf1 . size ) ; cl_assert_equal_i ( 8 , buf1 . asize ) ; cl_git_pass ( git_str_grow_by ( & buf1 , 16 ) ) ; cl_assert_equal_i ( 5 , buf1 . size ) ; cl_assert ( buf1 . asize > 8 ) ; } 