static void do_pf_error ( const char * msg , unsigned long error_code , unsigned long address , unsigned long ip ) { error_putstr ( msg ) ; error_putstr ( "\nError Code: " ) ; error_puthex ( error_code ) ; error_putstr ( "\nCR2: 0x" ) ; error_puthex ( address ) ; error_putstr ( "\nRIP relative to _head: 0x" ) ; error_puthex ( ip - ( unsigned long ) _head ) ; error_putstr ( "\n" ) ; } 