BMS_Membership bms_membership ( const Bitmapset * a ) { BMS_Membership result = BMS_EMPTY_SET ; int nwords ; int wordnum ; nwords = a -> nwords ; for ( wordnum = 0 ; wordnum < nwords ; wordnum ++ ) { bitmapword w = a -> words [ wordnum ] ; if ( w != 0 ) { if ( result != BMS_EMPTY_SET || HAS_MULTIPLE_ONES ( w ) ) { return BMS_MULTIPLE ; } result = BMS_SINGLETON ; } } return result ; } 