int getday ( char * s ) { struct fixs * n ; for ( n = fndays ; n -> name ; ++ n ) { if ( ! strncasecmp ( s , n -> name , n -> len ) ) { return ( ( n - fndays ) + 1 ) ; } } for ( n = ndays ; n -> name ; ++ n ) { if ( ! strncasecmp ( s , n -> name , n -> len ) ) { return ( ( n - ndays ) + 1 ) ; } } for ( p = days ; * p ; ++ p ) { if ( ! strncasecmp ( s , * p , 3 ) ) { return ( ( p - days ) + 1 ) ; } } return ( 0 ) ; } 