static void pptp_call_ppp_input ( pptp_call * _this , u_char * pkt , int pktlen , int delayed ) { int rval ; npppd_ppp * ppp ; ppp = _this -> ppp ; if ( ppp == NULL ) { pptp_call_log ( _this , LOG_WARNING , "Received ppp frame but ppp is not assigned yet" ) ; return ; } rval = ppp -> recv_packet ( ppp , pkt , pktlen , delayed ?PPP_IO_FLAGS_DELAYED : 0 ) ; if ( rval != 0 ) { ppp -> ierrors ++ ; } else { ppp -> ipackets ++ ; ppp -> ibytes += pktlen ; } } 