static void init_dinode ( struct gfs2_inode * dip , struct gfs2_inode * ip , const char * symname ) { struct gfs2_dinode * di ; struct buffer_head * dibh ; dibh = gfs2_meta_new ( ip -> i_gl , ip -> i_no_addr ) ; gfs2_trans_add_meta ( ip -> i_gl , dibh ) ; di = ( gfs2_dinode * ) dibh -> b_data ; gfs2_dinode_out ( ip , di ) ; di -> di_major = cpu_to_be32 ( MAJOR ( ip -> i_inode . i_rdev ) ) ; di -> di_minor = cpu_to_be32 ( MINOR ( ip -> i_inode . i_rdev ) ) ; di -> __pad1 = 0 ; di -> __pad2 = 0 ; di -> __pad3 = 0 ; memset ( & di -> __pad4 , 0 , sizeof ( di -> __pad4 ) ) ; memset ( & di -> di_reserved , 0 , sizeof ( di -> di_reserved ) ) ; gfs2_buffer_clear_tail ( dibh , sizeof ( gfs2_dinode ) ) ; switch ( ip -> i_inode . i_mode & S_IFMT ) { case S_IFDIR : break ; case S_IFLNK : memcpy ( dibh -> b_data + sizeof ( gfs2_dinode ) , symname , ip -> i_inode . i_size ) ; break ; } set_buffer_uptodate ( dibh ) ; brelse ( dibh ) ; } 