static void ispif_config_pack ( struct ispif_device * ispif , u32 code , enum ispif_intf intf , u8 cid , u8 vfe , u8 enable ) { u32 addr , val ; switch ( intf ) { case RDI0 : if ( cid < 8 ) { addr = ISPIF_VFE_m_RDI_INTF_n_PACK_CFG_0 ( vfe , 0 ) ; } else { addr = ISPIF_VFE_m_RDI_INTF_n_PACK_CFG_1 ( vfe , 0 ) ; } break ; case RDI1 : if ( cid < 8 ) { addr = ISPIF_VFE_m_RDI_INTF_n_PACK_CFG_0 ( vfe , 1 ) ; } else { addr = ISPIF_VFE_m_RDI_INTF_n_PACK_CFG_1 ( vfe , 1 ) ; } break ; case RDI2 : if ( cid < 8 ) { addr = ISPIF_VFE_m_RDI_INTF_n_PACK_CFG_0 ( vfe , 2 ) ; } else { addr = ISPIF_VFE_m_RDI_INTF_n_PACK_CFG_1 ( vfe , 2 ) ; } break ; default : return ; } if ( enable ) { val = ISPIF_VFE_m_RDI_INTF_n_PACK_CFG_0_CID_c_PLAIN ( cid ) ; } else { val = 0 ; } writel_relaxed ( val , ispif -> base + addr ) ; } 