static void rk3228_set_to_rgmii ( struct rk_priv_data * bsp_priv , int tx_delay , int rx_delay ) { struct device * dev = & bsp_priv -> pdev -> dev ; if ( IS_ERR ( bsp_priv -> grf ) ) { dev_err ( dev , "Missing rockchip,grf property\n" ) ; return ; } regmap_write ( bsp_priv -> grf , RK3228_GRF_MAC_CON0 , RK3228_GMAC_CLK_RX_DL_CFG ( rx_delay ) | RK3228_GMAC_CLK_TX_DL_CFG ( tx_delay ) ) ; } 