static void simple_line ( const char * block , const char * * names , const char * extra ) { size_t ii ; fprintf ( stdout , "# %s\n%s {\n" , orig_line , block ) ; for ( ii = 0 ; ii < nfields && names [ ii ] ; ++ ii ) { if ( names [ ii ] [ 0 ] == '#' ) { fprintf ( stdout , "\t%s = %s;\n" , names [ ii ] + 1 , fields [ ii ] ) ; } else { fprintf ( stdout , "\t%s = \"%s\";\n" , names [ ii ] , fields [ ii ] ) ; } } if ( extra ) { fprintf ( stdout , "\t%s\n" , extra ) ; } fputs ( "};\n" , stdout ) ; } 