void sbdp_showbd_2_sbd_stat ( show_board_t * shbp , sbd_stat_t * stp , int board ) { static fn_t f = "sbdp_showbd_2_sbd_stat" ; SBDP_DBG_FUNC ( "%s\n" , f ) ; stp -> s_board = board ; ( void ) strcpy ( stp -> s_info , shbp -> s_info ) ; stp -> s_power = shbp -> s_power ; ( void ) strcpy ( stp -> s_type , shbp -> s_type ) ; if ( shbp -> s_present == 0 ) { stp -> s_rstate = SBD_STAT_EMPTY ; } if ( shbp -> s_claimed == 0 ) { stp -> s_rstate = SBD_STAT_DISCONNECTED ; } else { stp -> s_rstate = SBD_STAT_CONNECTED ; } stp -> s_assigned = shbp -> s_assigned ; stp -> s_cond = shbp -> s_cond ; } 