static void uwsgi_lua_load_from_usl ( lua_State * L , struct uwsgi_string_list * usl ) { while ( usl ) { if ( usl -> value [ 0 ] ) { if ( luaL_loadfile ( L , usl -> value ) || lua_pcall ( L , 0 , 0 , 0 ) ) { ulua_log ( "ERROR: unable to load Lua file %s: %s" , usl -> value , lua_tostring ( L , - 1 ) ) ; lua_pop ( L , 1 ) ; } } usl = usl -> next ; } lua_gc ( L , LUA_GCCOLLECT , 0 ) ; lua_gc ( L , LUA_GCCOLLECT , 0 ) ; } 