static struct fpga_irq get_fpga_irq ( unsigned int irq ) { switch ( irq ) { case IRQ0_IRQ : set . sraddr = IRQ0_SR ; set . mraddr = IRQ0_MR ; set . mask = IRQ0_MASK ; set . base = IRQ0_BASE ; break ; case IRQ1_IRQ : set . sraddr = IRQ1_SR ; set . mraddr = IRQ1_MR ; set . mask = IRQ1_MASK ; set . base = IRQ1_BASE ; break ; default : set . sraddr = IRQ2_SR ; set . mraddr = IRQ2_MR ; set . mask = IRQ2_MASK ; set . base = IRQ2_BASE ; break ; } return set ; } 