void tape_dump_sense_dbf ( struct tape_device * device , struct tape_request * request , struct irb * irb ) { unsigned int * sptr ; const char * op ; if ( request != NULL ) { op = tape_op_verbose [ request -> op ] ; } else { op = "---" ; } DBF_EVENT ( 3 , "DSTAT : %02x   CSTAT: %02x\n" , irb -> scsw . cmd . dstat , irb -> scsw . cmd . cstat ) ; DBF_EVENT ( 3 , "DEVICE: %08x OP\t: %s\n" , device -> cdev_id , op ) ; DBF_EVENT ( 3 , "%08x %08x\n" , sptr [ 0 ] , sptr [ 1 ] ) ; DBF_EVENT ( 3 , "%08x %08x\n" , sptr [ 2 ] , sptr [ 3 ] ) ; DBF_EVENT ( 3 , "%08x %08x\n" , sptr [ 4 ] , sptr [ 5 ] ) ; DBF_EVENT ( 3 , "%08x %08x\n" , sptr [ 6 ] , sptr [ 7 ] ) ; } 